    var leftNavFlag = true;
    class LeftNav {
        constructor() {
            this.init();
        }

        init() {
            $(".left-nav li").on("click", this.documentScroll);
            $(window).on("scroll", this.windowScroll);
        }

        documentScroll(e) {
            var that = this;
            if (leftNavFlag) {
                leftNavFlag = false;
                e.stopPropagation();
                var destination = $(this).attr("data-destination");
                var goTo = $("." + destination).first().offset().top - 30;
                $("body, html").stop().animate({
                    scrollTop: goTo
                }, 1000, function () {
                    leftNavFlag = true;
                    $(".left-nav li").removeClass("active");
                    $(that).addClass("active");
                });
            }
        }

        windowScroll() {
            var whereIm = "";
            $("div[data-evelator]").each(function (i, ele) {
                if ($(document).scrollTop() >= $(ele).offset().top - 40) {
                    whereIm = $(ele).attr("class");
                }
            });
            $(".left-nav li").removeClass("active");
            $(`.left-nav li[data-destination="${whereIm}"]`).addClass("active");
        }
    }
    new LeftNav();
